(function(w,d,config,undefined){function calculateScreenWidth(){var width;if(typeof window.orientation==="undefined"){width=w.screen.width||0}else{width=w.innerWidth||d.documentElement.clientWidth||d.body.clientWidth||0;window.onorientationchange=function(){window.setTimeout(function(){apply()},500)}}return width}function getCookie(name){var nameEQ=name+"=";var ca=document.cookie.split(";");for(var i=0;i<ca.length;i++){var c=ca[i];while(c.charAt(0)==" ")c=c.substring(1,c.length);if(c.indexOf(nameEQ)==0)return c.substring(nameEQ.length,c.length)}return null}function setScreenWidthInCookie(screenWidth){document.cookie="screenWidth="+screenWidth+"; path=/"}function apply(){var screenWidth=calculateScreenWidth();var storedScreenWidth=parseInt(getCookie("screenWidth"),10);if(screenWidth!==storedScreenWidth){setScreenWidthInCookie(screenWidth);storedScreenWidth=parseInt(getCookie("screenWidth"),10);if(!storedScreenWidth){return}for(var i=0;i<config.profiles.length;i++){var profile=config.profiles[i];if(profile.minimumWidth<screenWidth&&screenWidth<=profile.maximumWidth){if(!profile.isCurrentProfile){location.reload(true)}break}}}}apply()})(this,this.document,BN_ADAPTIVE_PROFILES_CONFIG);